export type ThemeTokenName = keyof typeof themeTokens;

export function resolveThemeColorToken(
  name: ThemeTokenName,
  theme: Theme,
  accent: Accent,
): ColorTokenValue {
  const token = themeTokens[name][theme];
  if ("rawColor" in token) return token;

  return token[accent];
}

